package Loops;

public class total_sales {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] dailySales= {1200,900,100,200,2000,3000,4000};
		int totalSales=0;
		
		for (int i=0;i<1;i++)
		{
			totalSales += dailySales[i];
		}
		System.out.println("Total Sle of Week:"+totalSales);
		

	}

}
